import React, { useState } from 'react';
import reactDom from 'react-dom';
import { Container } from 'reactstrap';


export const Home = () => {


    const [bitaoDeleteIsVisible, setBitaoDeleteIsVisible]=useState(true);

    const [bitaoDeleteSimIsVisible, setBitaoDeleteSimIsVisible]=useState(true);

    const [bitaoDeleteNaoIsVisible, setBitaoDeleteNaoIsVisible]=useState(true);

    const [bitaoDeleteCtzaIsVisible, setBitaoDeleteCtzaIsVisible]=useState(true);

    function mudarBitaoDeleteInvisivel (){
        setBitaoDeleteIsVisible(!bitaoDeleteIsVisible);
    }

    function mudarBitaoDeleteSimInvisivel (){
        setBitaoDeleteSimIsVisible(!bitaoDeleteSimIsVisible);
    }

    function mudarBitaoDeleteNaoInvisivel (){
        setBitaoDeleteNaoIsVisible(!bitaoDeleteNaoIsVisible);
    }

    function mudarBitaoDeleteCtzaInvisivel (){
        setBitaoDeleteCtzaIsVisible(!bitaoDeleteCtzaIsVisible);
    }

    
    function juntarFunctions (){

        mudarBitaoDeleteInvisivel();

        mudarBitaoDeleteSimInvisivel();

        mudarBitaoDeleteNaoInvisivel();

        mudarBitaoDeleteCtzaInvisivel();
    }

    return (
        <div>
            <Container>
                <div className="d-flex flex-column justify-content-center align-items-center">
                    <div className="w-100 m-auto mt-3 p-3 border rounded-pill d-flex justify-content-center align-items-center" style={{ background: '#23bceb' }}>
                        <div className="m-auto p-2">
                            <h1>Página inicial</h1>
                        </div>
                    </div>
                    <div className="d-flex justify-content-center">
                        <div className="p-2">
                            <a href="/visualizarcliente" className="btn btn-outline-primary btn-lg">Cliente</a>
                        </div>
                        <div className="p-2">
                            <a href="/visualizarservico" className="btn btn-outline-primary btn-lg">Serviço</a>
                        </div>
                        <div className="p-2">
                            <a href="/visualizarpedido" className="btn btn-outline-primary btn-lg">Pedido</a>
                        </div>
                    </div>

                    <div id="renderizarCerteza" className="d-flex justify-content-center d-none">
                    <span className="btn btn-outline-danger btn-sm disabled">Tem certeza?</span>
                    </div>

                    <div className="d-flex justify-content-center align-items-center">
                        <div className="p-2">
                            <span onClick={juntarFunctions} className={bitaoDeleteIsVisible ? "btn btn-outline-primary btn-lg"
                            : "btn btn-outline-primary btn-lg d-none"}>Bitao teste</span>
                        </div>

                        <div id="renderizarBitaoSim" className="p-2">
                        <span className="btn btn-outline-danger btn-lg d-none">Sim</span>
                        </div>

                        <div id="renderizarBitaoNao" className="p-2">
                        <span className="btn btn-outline-success btn-lg d-none">Não</span>
                        </div>
                    </div>

                    {/* <div className="d-flex justify-content-center align-items-center">
                    <span onClick={mudarInvisivel} className="btn btn-outline-primary btn-lg">Controle</span>
                    </div> */}


                </div>
            </Container>
        </div>
    )
}
