import React, { useState } from 'react';
import reactDom from 'react-dom';
import { Container } from 'reactstrap';


export const Home = () => {

    function teste (){
        console.log("Clicou!");
        let certeza=React.createElement("span", {id: "ctza", className: "btn btn-outline-danger btn-sm disabled"}, "Tem certeza?");
        let bitaoSim=React.createElement("span", {id: "bitaosim", className: "btn btn-outline-danger btn-lg"}, "Sim");
        let bitaoNao=React.createElement("span", {id: "bitaonao", className: "btn btn-outline-success btn-lg", onClick: juntarFunctionsORetorno }, "NÃ£o");
        reactDom.render(certeza, document.getElementById("renderizarCerteza"));
        reactDom.render(bitaoSim, document.getElementById("renderizarBitaoSim"));
        reactDom.render(bitaoNao, document.getElementById("renderizarBitaoNao"));
    }

    function destroyUsAll (){
        document.getElementById("ctza").remove();
        document.getElementById("bitaosim").remove();
        document.getElementById("bitaonao").remove();
    }

    function testimuxus (){
        console.log("Clicaralhomus!");
    }

    const [isVisible, setIsVisible]=useState(true);


    function mudarInvisivelFalse (){
        setIsVisible(false);
    }

    function mudarInvisivelTrue (){
        setIsVisible(true);
    }

    function juntarFunctions (){

        teste();

        mudarInvisivelFalse();
    }

    function juntarFunctionsORetorno (){

        destroyUsAll();

        mudarInvisivelTrue();
    }

    return (
        <div>
            <Container>
                <div className="d-flex flex-column justify-content-center align-items-center">
                    <div className="w-100 m-auto mt-3 p-3 border rounded-pill d-flex justify-content-center align-items-center" style={{ background: '#23bceb' }}>
                        <div className="m-auto p-2">
                            <h1>PÃ¡gina inicial</h1>
                        </div>
                    </div>
                    <div className="d-flex justify-content-center">
                        <div className="p-2">
                            <a href="/visualizarcliente" className="btn btn-outline-primary btn-lg">Cliente</a>
                        </div>
                        <div className="p-2">
                            <a href="/visualizarservico" className="btn btn-outline-primary btn-lg">ServiÃ§o</a>
                        </div>
                        <div className="p-2">
                            <a href="/visualizarpedido" className="btn btn-outline-primary btn-lg">Pedido</a>
                        </div>
                    </div>

                    <div id="renderizarCerteza" className="d-flex justify-content-center">

                    </div>

                    <div className="d-flex justify-content-center align-items-center">
                        <div className="p-2">
                            <span onClick={juntarFunctions} className={isVisible ? "btn btn-outline-primary btn-lg"
                            : "btn btn-outline-primary btn-lg d-none"}>Bitao teste</span>
                        </div>
                        <div id="renderizarBitaoSim" className="p-2">
                            
                        </div>
                        <div id="renderizarBitaoNao" className="p-2">

                        </div>
                    </div>


                </div>
            </Container>
        </div>
    )
}
