import axios from 'axios';
import { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import { Container } from 'reactstrap';
import { api } from '../../../config';

export const Pedido = (props) => {
    console.log(props.match.params.id);

    const [data, setData] = useState([]);

    const [dataCliente, setDataCliente] = useState([]);
    const [nomeCliente, setNomeCliente] = useState('');

    const [dataServico, setDataServico] = useState([]);
    const [nomeServico, setNomeServico] = useState('');

    const [id, setID] = useState(props.match.params.id);

    const [idCliente, setIdCliente] = useState([]);
    const [idServico, setIdServico] = useState([]);

    useEffect(() => {
        const getPedido = async () => {
            await axios.get(api + "/pedido/" + id)
                .then((response) => {
                    console.log(response.data.pedido);
                    setData(response.data.pedido);
                    setIdCliente(data.ClienteId);
                    setIdServico(data.ServicoId);
                })
                .catch(() => {
                    console.log("Erro: Não foi possível conectar a API.");
                })
        }
        getPedido();
    }, [id]);

    useEffect(() => {
        const getCliente = async () => {
            await axios.get(api + "/cliente/" + idCliente)
                .then((response) => {
                    console.log(response.data.cliente);
                    setDataCliente(response.data.cliente);
                    setNomeCliente(response.data.cliente.nome);
                })
                .catch(() => {
                    console.log("Erro: Não foi possível conectar a API.");
                })
        }
        getCliente();
    }, [idCliente]);

    useEffect(() => {
        const getServico = async () => {
            await axios.get(api + "/servico/" + idServico)
                .then((response) => {
                    console.log(response.data.servico);
                    setDataServico(response.data.servico);
                    setNomeServico(response.data.servico.nome);
                })
                .catch(() => {
                    console.log("Erro: Não foi possível conectar a API.");
                })
        }
        getServico();
    }, [idServico]);

    return (
        <div>
            <Container>
                <div className="d-flex flex-column justify-content-center align-items-center">
                    <div className="w-100 m-auto mt-3 p-3 border rounded-pill d-flex justify-content-center align-items-center" style={{ background: 'rgba(16,100,199,0.33)' }}>
                        <div className="m-auto p-2">
                            <h1>Informações do Pedido</h1>
                        </div>
                    </div>
                    <div className="w-75 mt-3 d-flex flex-column justify-content-center align-items-center">
                        <div className="w-100 p-5 border border-dark rounded" style={{ background: 'rgba(16,199,154,0.33)' }}>
                            <dl className="row">
                                <dt className="col-md-3">ID do Cliente</dt>
                                <dd className="col-md-3">{data.ClienteId}</dd>
                            </dl>
                            <dl className="row">
                                <dt className="col-md-3">Nome do Cliente</dt>
                                <dd className="col-md-3">{nomeCliente}</dd>
                            </dl>
                            <dl className="row">
                                <dt className="col-md-3">Consultar Cliente</dt>
                                <dd className="col-md-3"><Link to={"/cliente/" + idCliente} className="btn btn-outline-primary btn-md">Consultar este cliente</Link></dd>
                            </dl>
                            <dl className="row">
                                <dt className="col-md-3">ID do Serviço</dt>
                                <dd className="col-md-3">{data.ClienteId}</dd>
                            </dl>
                            <dl className="row">
                                <dt className="col-md-3">Nome do Serviço</dt>
                                <dd className="col-md-3">{nomeServico}</dd>
                            </dl>
                            <dl className="row">
                                <dt className="col-md-3">Consultar Serviço</dt>
                                <dd className="col-md-3"><Link to={"/servico/" + idServico} className="btn btn-outline-primary btn-md">Consultar este serviço</Link></dd>
                            </dl>
                            <dl className="row">
                                <dt className="col-md-3">Valor</dt>
                                <dd className="col-md-3">{data.valor}</dd>
                            </dl>
                            <dl className="row">
                                <dt className="col-md-3">Data</dt>
                                <dd className="col-md-3">{data.data}</dd>
                            </dl>
                            <dl className="row">
                                <dt className="col-md-3">Data de Criação</dt>
                                <dd className="col-md-3">{data.createdAt}</dd>
                            </dl>
                        </div>
                    </div>
                    <div className="mt-2 d-flex justify-content-center">
                        <div className="p-2">
                            <Link to="/visualizarpedido" className="btn btn-outline-primary btn-sm">Voltar</Link>
                        </div>
                    </div>
                </div>
            </Container>
        </div>
    );
}
