import React, { useState } from 'react';
import reactDom from 'react-dom';
import { Container } from 'reactstrap';


export const Home = () => {

    function teste (){
        console.log("Clicou!");
        let certeza=React.createElement("span", {className: "btn btn-outline-danger btn-sm disabled"}, "Tem certeza?");
        let bitaoSim=React.createElement("span", {className: "btn btn-outline-danger btn-lg"}, "Sim");
        let bitaoNao=React.createElement("span", {className: "btn btn-outline-success btn-lg", onClick:{testimuxus}}, "Não");
        reactDom.render(certeza, document.getElementById("renderizarCerteza"));
        reactDom.render(bitaoSim, document.getElementById("renderizarBitaoSim"));
        reactDom.render(bitaoNao, document.getElementById("renderizarBitaoNao"));
    }

    function testimuxus (){
        console.log("Clicaralhomus!");
    }

    const [isVisible, setIsVisible]=useState(true);


    function mudarInvisivel (){
        setIsVisible(!isVisible);
    }

    function juntarFunctions (){

        teste();

        mudarInvisivel();
    }

    return (
        <div>
            <Container>
                <div className="d-flex flex-column justify-content-center align-items-center">
                    <div className="w-100 m-auto mt-3 p-3 border rounded-pill d-flex justify-content-center align-items-center" style={{ background: '#23bceb' }}>
                        <div className="m-auto p-2">
                            <h1>Página inicial</h1>
                        </div>
                    </div>
                    <div className="d-flex justify-content-center">
                        <div className="p-2">
                            <a href="/visualizarcliente" className="btn btn-outline-primary btn-lg">Cliente</a>
                        </div>
                        <div className="p-2">
                            <a href="/visualizarservico" className="btn btn-outline-primary btn-lg">Serviço</a>
                        </div>
                        <div className="p-2">
                            <a href="/visualizarpedido" className="btn btn-outline-primary btn-lg">Pedido</a>
                        </div>
                    </div>

                    <div id="renderizarCerteza" className="d-flex justify-content-center">

                    </div>

                    <div className="d-flex justify-content-center align-items-center">
                        <div className="p-2">
                            <span onClick={juntarFunctions} className={isVisible ? "btn btn-outline-primary btn-lg"
                            : "btn btn-outline-primary btn-lg d-none"}>Bitao teste</span>
                        </div>
                        <div id="renderizarBitaoSim" className="p-2">
                            
                        </div>
                        <div id="renderizarBitaoNao" className="p-2">

                        </div>
                    </div>


                </div>
            </Container>
        </div>
    )
}
